use websales
go

create table Categories
(
    Id     int identity
        constraint PK_Categories
        primary key,
    Name   nvarchar(50) not null,
    NameVN nvarchar(50) not null
)
    go


use websales
go

create table Products
(
    Id          int identity
        constraint PK_Products
        primary key,
    Name        nvarchar(60) not null,
    Image       nvarchar(50) not null,
    UnitPrice   float        not null,
    Discount    float        not null,
    Quantity    int          not null,
    ProductDate date         not null,
    Available   bit          not null,
    CategoryId  int          not null,
    Description nvarchar(max),
    ViewCount   int
)
    go



use websales
go

create table Customers
(
    Id               nvarchar(20) not null
        constraint PK_Customers
        primary key,
    Password         nvarchar(50) not null,
    Fullname         nvarchar(50) not null,
    Email            nvarchar(50) not null,
    Photo            nvarchar(50) not null,
    Activated        bit          not null,
    Admin            bit          not null,
    IsChangePassword bit,
    PhoneNumber      varchar(255),
    Address          nvarchar(255)
)
    go


use websales
go

create table Partners
(
    id             int identity
        constraint Partners_pk
        primary key nonclustered,
    created_by     varchar(255),
    modified_by    varchar(255),
    modified_date  varchar(255),
    state          varchar(255),
    code           varchar(255),
    generate_uri   varchar(255),
    host           varchar(255),
    merchant_id    varchar(255),
    name           nvarchar(50),
    redirect_proxy varchar(255),
    uuid           varchar(255),
    created_date   varchar(255),
    secret_key     varchar(255)
)
    go

create unique index Partners_id_uindex
    on Partners (id)
go


use websales
go

create table Orders
(
    Id          int identity
        constraint PK_Orders
        primary key,
    CustomerId  nvarchar(20) not null,
    OrderDate   datetime     not null,
    Address     nvarchar(60) not null,
    Amount      float        not null,
    Description nvarchar(1000),
    Status      int,
    IsPay       bit,
    PartnerId   int
)
    go




use websales
go

create table OrderDetails
(
    Id        int identity
        constraint PK_OrderDetails
        primary key,
    OrderId   int   not null,
    ProductId int   not null,
    UnitPrice float not null,
    Quantity  int   not null,
    Discount  float not null
)
    go



use websales
go

create table Transactions
(
    id                       int identity,
    created_by               varchar(255),
    created_date             varchar(255),
    modified_by              varchar(255),
    modified_date            varchar(255),
    state                    varchar(255),
    amount                   varchar(255),
    bank_code                varchar(255),
    bank_transaction_id      varchar(255),
    card_no                  varchar(255),
    card_type                varchar(255),
    client_invoice_id        varchar(255),
    currency_code            varchar(255),
    description              nvarchar(255),
    partner_status           varchar(255),
    partner_transaction_date varchar(255),
    partner_transaction_id   varchar(255),
    status                   varchar(255),
    transaction_id           varchar(255),
    username                 varchar(255),
    partner_id               int,
    location                 varchar(255)
)
    go

create unique index Transactions_id_uindex
    on Transactions (id)
